const isDev = process.env.NODE_ENV !== 'production';
export default context => {
    return new Promise((resolve, reject) => {
        const s = isDev && Date.now();
        const { app, router, store, preFetchComponent } = createApp();
        const { url } = context;
        const { fullPath } = router.resolve(url).route;
        if (fullPath !== url) {
            return reject({ url: fullPath });
        }
        router.push(url);
        context.meta = app.$meta();
        store.state.supportWebp = context.supportWebp;
        router.onReady(() => {
            const matchedComponents = router.getMatchedComponents();
            if (!matchedComponents.length)
                return reject({ code: 404 });
            Promise.all(preFetchComponent.concat(matchedComponents).map(({ options: { asyncData } }) => asyncData && asyncData({
                store,
                route: router.currentRoute
            }))).then(() => {
                isDev && console.log(`data pre-fetch: ${Date.now() - s}ms`);
                context.state = store.state;
                resolve(app);
            }).catch(reject);
        }, reject => {
            console.error(Date.now().toLocaleString(), reject);
        });
    });
};

